function modifier_silencer_last_word:DeclareFunctions()
	return {
		MODIFIER_EVENT_ON_ABILITY_END_CHANNEL,
	}
end
function modifier_silencer_last_word:OnAbilityEndChannel(params)
	if IsServer() then
		if params.unit==self:GetParent() and not params.ability:IsItem() then
			self:Destroy()
		end
	end
end

function modifier_razor_eye_of_the_storm_attack_effect_imba:DeclareFunctions()
	return {
		MODIFIER_PROPERTY_OVERRIDE_ATTACK_DAMAGE,
	}
end
function modifier_razor_eye_of_the_storm_attack_effect_imba:GetModifierOverrideAttackDamage(params)
	return self:GetAbilitySpecialValueFor("damage")
end

function modifier_respawntime_percent:DeclareFunctions()
	return {
		MODIFIER_PROPERTY_RESPAWNTIME_PERCENTAGE
	}
end

function modifier_respawntime_percent:GetModifierPercentageRespawnTime(params)
	return 1 - (0.5 + self:GetParent():GetLevel() / 100)
end

